mat = %w(










).map(&:chars)

cols = mat.first.size

cons = []

profile = {
  'A' => [],
  'C' => [],
  'G' => [],
  'T' => [],
}

mat.each do |row|
  row.each_with_index do |x, i|
    cons[i] ||= {}
    cons[i][x] ||= 0
    cons[i][x] += 1

    profile[x][i] ||= 0
    profile[x][i] += 1
  end
end

puts cons.map { |xs| xs.max_by { |(k,v)| v }[0] }.join
%w( A C G T ).each do |c|
  printf("%s: %s\n", c, profile[c].map { |g| g ? g : 0 }.join(' '))
end
  
